sourcesContent:
/**
 * CheckOrderType
 *
 * Props: ordersBook, dealId, direction
 * Cerco all'interno della lista ordini, l'ordine appena eseguito per identificarne il tipo
 *
 * Return: OrderType
 * */
export const CheckOrderType = async (ordersBook, level) => {
  let orderType

  for (const order of ordersBook.orders) {
    if (order.level === level) {
      orderType = order.type
    }
  }

  return orderType
}

/**
 **/
export const CloseUnusedOrders = async (ordersBook, openPrice, client) => {
  console.log("-- CloseUnusedOrders start--")
  if (ordersBook.orders.length > 0) {
    for (const order of ordersBook.orders) {
      if (order.level !== openPrice) {
        try {
          await client.rest.dealing.deleteOrder(order.dealId)
            .then((res) => {
              console.log("chiuso ordine | dealReference::", res)
            })
        }catch (e) {
          console.log("errore chiusura ordine", e)
        }
      }
    }
  }
  console.log("-- CloseUnusedOrders end--")
}

/**
 * */
export const CloseAllPositions = async (positionsBook, client) => {
  console.log("-- CloseAllPositions --")
  if (positionsBook.trendPositions.length > 0) {
    for (const position of positionsBook.trendPositions) {
      ClosePosition(position, client)
        .then((res) => {
          console.log("chiusa posizione | dealReference::", res)
        })
    }
  }
  if (positionsBook.coverPositions.length > 0) {
    for (const position of positionsBook.coverPositions) {
      ClosePosition(position, client)
        .then((res) => {
          console.log("Chiusa posizione | dealReference::", res)
        })
    }
  }
  //console.log("----")
}

/**
 * CreatePosition
 *
 * */
export const CreatePosition = async (epic, direction, size, currency, client) => {
  const data = {
    epic: epic,
    expiry: '-',
    direction: direction,
    size: size,
    forceOpen: 'true',
    orderType: 'MARKET',
    currencyCode: currency,
    timeInForce: 'EXECUTE_AND_ELIMINATE',
    guaranteedStop: 'false',
    stopLevel: null,
    stopDistance: null,
    limitLevel: null,
    limitDistance: null,
  }

  console.log("-- CreatePosition --")
  const dealReference = await client.rest.dealing.createPosition(data)
  console.log("Apro nuova posizione | dealReference:", dealReference)
  const res = await client.rest.dealing.confirmTrade(dealReference)
  console.log("---")
  return res
}

/**
 * */
export const CreateOrder = async (direction, epic, currency, level, limitlevel, stopLevel, size, type, client) => {
  const data = {
    epic: epic,
    expiry: '-',
    direction: direction,
    size: size,
    level: level.toFixed(4),
    forceOpen: 'true',
    type: type,
    currencyCode: currency,
    timeInForce: 'GOOD_TILL_CANCELLED',
    goodTillDate: null,
    guaranteedStop: 'false',
    stopLevel: null,
    stopDistance: stopLevel,
    limitLevel: null,
    limitDistance: limitlevel,
  }

  console.log("-- CreateOrder --")
  const dealReference = await client.rest.dealing.createOrder(data)
  console.log("inserito nuovo ordine | dealReference:", dealReference)
  const res = await client.rest.dealing.confirmTrade(dealReference)
  console.log("---")
  return res
}

export const ClosePosition = async (position, partial, client) => {
  const data = {
    dealId: position.dealId,
    epic: null,
    expiry: null,
    direction: position.direction === "BUY" ? "SELL" : "BUY",
    size: partial ? position.size/2 : position.size,
    level: null,
    orderType: "MARKET",
    timeInForce: "EXECUTE_AND_ELIMINATE",
    quoteId: null
  }


  console.log("-- ClosePosition start--")
  const dealReference = await client.rest.dealing.closePosition(data)
  console.log("Chiusa posizione | dealReference:", dealReference)
  const res = await client.rest.dealing.confirmTrade(dealReference)
  console.log("-- ClosePosition end--")
  return res
}

module.exports = {
  CheckOrderType,
  CloseAllPositions,
  CloseUnusedOrders,
  CreateOrder,
  CreatePosition,
  ClosePosition
}

